/**
 * Custom RTE insert phone number implementation
 */
barclays.componentlibrary.rte.phoneRef = (function() {
    'use strict';

    var init = function(){
        // get all phone number references and process...
        $('img.cq-rte-insertref').each(function(){
            // get contact card URL and itemId
            var thisRef = this;
            var querystring = $(thisRef).attr('src').split('?').pop();
            var valuePairs = querystring.split('&');
            var obj = {};
            valuePairs.forEach(function(pair){
                var splitPair = pair.split('=');
                obj[splitPair[0]] = splitPair[1];
            });

            if ((typeof obj['data-href'] !== 'undefined') && (typeof obj['data-phone'] !== 'undefined')){
                // get contact card json data
                var jsonPath = obj['data-href'].replace('.html', '') + '/jcr:content/contact_detail.json';
                $.getJSON(jsonPath, function () {
                        //console.log('JSON request success');
                    })
                    .done(function (data) {
                        var phones = [];
                        if (typeof data.phones == 'string'){
                            phones.push($.parseJSON(data.phones));
                        } else {
                            data.phones.forEach(function (phone) {
                                phones.push($.parseJSON(phone));
                            });
                        }
                        var matchPhone = arrayLookup(phones, 'itemId', obj['data-phone']);
                        //replace img with phone number.
                        $(thisRef).replaceWith(matchPhone.phoneValue);
                    })
                    .fail(function () {
                        console.log('JSON request error');
                    });
            }

        });
    };

    var arrayLookup = function (array, prop, val) {
        for (var i = 0, len = array.length; i < len; i++) {
            if (array[i].hasOwnProperty(prop) && array[i][prop] === val) {
                return array[i];
            }
        }
        return null;
    };

    return {
        init : init
    };

})(jQuery);

barclays.componentlibrary.rte.phoneRef.init();
